    createLanguageList: function (defaultValue = null) {
        let optionContainer = document.getElementById("languages");
        if (optionContainer) {
            let selectFragment = document.createDocumentFragment();
            this.languages.forEach((lang) => {
                var el = document.createElement("option");
                el.textContent = lang["country"];
                el.value = lang["lang"];
                if (defaultValue && (defaultValue === lang["lang"] || lang["lang"].startsWith(defaultValue + "-"))) {
                    el.selected = "selected";
                    this.createVoiceList(lang["lang"]);
                }
                selectFragment.appendChild(el);
            });
            optionContainer.appendChild(selectFragment);
        }
    },
    createVoiceList: function (langCode = null) {
        let optionContainer = document.getElementById("voices");
        if (optionContainer && langCode) {
            optionContainer.innerHTML = "";
            let selectFragment = document.createDocumentFragment();
            var setSelected = false;
            this.voices.forEach((lang) => {
                if (lang["langCode"] === langCode || lang["langCode"].startsWith(langCode + "-")) {
                    var el = document.createElement("option");
                    el.textContent = lang["name"] + " (" + lang["gender"] + ")";
                    el.value = lang["name"];
                    if (!setSelected) {
                        el.selected = "selected";
                        setSelected = true;
                    }
                    selectFragment.appendChild(el);
                }
            });
            optionContainer.appendChild(selectFragment);
        }
    },
    onLanguageChanged: function () {
        //console.log("select changed");
        window.myextension.createVoiceList(document.getElementById("languages").value);
    },
    updateOptionsData: function () {
        if (this.options && ("autoBreakLine" in this.options)) {
            this.options.language = document.getElementById("languages").value;
            this.options.voice = document.getElementById("voices").value;
            this.options.mainContentQuery = document.getElementById("mainContenId").value;
            this.options.nextButtonQuery = document.getElementById("nextContenId").value;
            this.options.forceUsingCustomFont = document.getElementById("forceUseFont").checked;
            this.options.autoBreakLine = document.getElementById("autoBreakLine").checked;
            this.options.fontFamily = document.getElementById("fonts").value;
            this.options.allowScrolling = document.getElementById("autoScroll").checked;
            this.options.autoConvert = document.getElementById("autoConvert").checked;
            this.options.showDetail = document.getElementById("showConvertDetail").checked;
            this.options.enableContextMenu = document.getElementById("enableContextMenu").checked;
        }
    },
